var error_message = ""; 
var INT_MAX = 9999;
var INT_MIN = 0;
var LIMIT_EXPR_CALL = -1;
function itos(n){
    return String(n);
}
function clean(s){
    if( s.indexOf(';') != -1 ){
        s = s.substr(0,s.indexOf(';')); 
    }
    s = s.replace( /\t/g,"");
    s = s.replace( /\r/g,"");
    s = s.replace( / /g,"");
    return s + "\n";
}

// Simulator class
function Simulator(inputs,lineNumbers) 
{
    this.N = -1;
    this.M = -1;
    this.X = -1;
    this.Y = -1;
    this.type = -1;
    this.pos = 0;
    this.variableA_isUsed = false;
    this.noEvaluationFlag = false;
    this.memory = new Object();
    this.loopEndMemo = [new Object(),new Object(),new Object()];
    this.input = ["","",""];
    this.lineNumber = [[],[],[]];

    for(var i = 0 ; i &lt; 3 ; i++){
        this.input[i] = inputs[i];
        this.lineNumber[i] = lineNumbers[i];
    }
    for(var i = 0 ; i &lt;= 9999 ; i++){
        this.memory["a["+itos(i)+"]"] = 0;
    }
    this.memory["A"] = 0;
    this.exprCalledCount = 0;
}

Simulator.prototype.setNM = function(n,m) {
    this.N = n;
    this.M = m;
}
Simulator.prototype.throwError = function(msg){

    error_message = msg + " (around line " + itos(this.lineNumber[this.type][this.pos]) + ")";
    throw "";
}

Simulator.prototype.start = function(type_,X_,Y_){
    this.type = type_;
    this.pos = 0;
    this.X = X_;
    this.Y = Y_;
    this.memory["A"] = 0;
    this.variableA_isUsed = false;
    this.program();
    if( this.pos == this.input[this.type].length )return this.memory["A"];
    else this.throwError("Syntax Error");
}

Simulator.prototype.nextChar = function(){
    return this.input[this.type][this.pos];
}
Simulator.prototype.nextIs = function(desired){
    return this.input[this.type][this.pos] == desired;
}
Simulator.prototype.secondNextIs = function(desired){
    return this.input[this.type][this.pos+1] == desired;
}

Simulator.prototype.nextIs = function(desired){
    for(var i=0,len=desired.length;i<len;i++){
        if(this.input[this.type][this.pos] == desired[i] ) return true;
    }
    return false;
}

Simulator.prototype.checkNum = function(num){
    if( this.noEvaluationFlag ) return;
    if( num > INT_MAX || num &lt; INT_MIN ){
        this.throwError("overflow");
    }
}

Simulator.prototype.forwardChar = function(desired){
    if( this.input[this.type][this.pos] == desired ){
        this.pos++;
    }else{
        this.throwError("Syntax Error");
    }
}
Simulator.prototype.forwardWord = function(desired){
    for(var i=0,len=desired.length;i<len;i++){
        this.forwardChar(desired[i]);
    }
}

Simulator.prototype.program = function(){
    this.command();
    while( this.nextIs('\n') ){
        // LL(2)っぽい
        if( this.secondNextIs('}') ){
            return;
        }else{
            this.forwardChar('\n');
            this.command();
        }
    }
}

Simulator.prototype.command = function(){
    if( this.nextIs('w') ){
        this.repetition();
    }else{
        this.assign();
    }
}
Simulator.prototype.assign = function(){
    var name = this.var();
    this.forwardChar('=');
    var rightSide = this.expr();
    if( !this.noEvaluationFlag )
        this.memory[name] = rightSide;
}
Simulator.prototype.repetition = function(){
    var loopBegin = this.pos;
    var loopEnd;
    // 一度だけは構文解析して解析位置を確かめるために、文法だけ精査する。意味解析はしない。

    if( !(loopBegin in this.loopEndMemo[this.type]) ){
        var prevFlag = this.noEvaluationFlag;
        this.noEvaluationFlag = true;
        this.forwardWord("while(");
        this.expr();
        this.forwardChar('&lt;');
        this.expr();
        this.forwardWord("){\n");
        this.program();
        this.forwardWord("\n}");
        this.loopEndMemo[this.type][loopBegin] = this.pos;
        this.noEvaluationFlag = prevFlag;
    }
    loopEnd = this.loopEndMemo[this.type][loopBegin];


    // 構文解析なら実際にループは実行しない
    if( this.noEvaluationFlag ) return;
    while(1){
        this.pos = loopBegin;
        //実際にループ実行
        this.forwardWord("while(");
        var leftSide = this.expr();
        this.forwardChar('&lt;');
        var rightSide = this.expr();

        if( leftSide >= rightSide ){
            this.pos = loopEnd;
            return;
        }else{
            this.forwardWord("){\n");
            this.program();
            this.forwardWord("\n}");
        }
    }
}
Simulator.prototype.exprCalledCountUp = function(){
    if( !this.noEvaluationFlag ){
        this.exprCalledCount++;
        if( this.exprCalledCount > LIMIT_EXPR_CALL ){
            this.throwError("Total number of <expr> call Limit Exceeded.");
        }
    }
}
Simulator.prototype.expr = function(){
    this.exprCalledCountUp();
    var res = this.param();
    while( this.nextIs("+-") ){
        this.exprCalledCountUp();
        if( this.nextIs('+') ){
            this.forwardChar('+');
            res = res + this.param();
            this.checkNum(res);
        }else{
            this.forwardChar('-');
            res = res - this.param();
            this.checkNum(res);
        }
    }
    return res;
}

Simulator.prototype.param = function(){
    if( this.nextIs("(") ){
        this.forwardChar('(');
        var val = this.expr();
        this.forwardChar(')');
        return val;
    }else if( this.nextIs("NMXY0123456789") ){
        if( this.nextIs('N') ){
            this.forwardChar('N');
            if( this.noEvaluationFlag ) return 0; // 評価しないとき全ての値は0になる
            return this.N;
        }else if( this.nextIs('M') ){
            this.forwardChar('M');
            if( this.noEvaluationFlag ) return 0; // 評価しないとき全ての値は0になる
            return this.M;
        }else if( this.nextIs('X') ){
            if( this.type == 0 ) this.throwError("Can't use constant 'X' in initialization.");
            this.forwardChar('X');
            if( this.noEvaluationFlag ) return 0; // 評価しないとき全ての値は0になる
            return this.X;
        }else if( this.nextIs('Y') ){
            if( this.type == 0 ) this.throwError("Can't use constant 'Y' in initialization.");
            this.forwardChar('Y');
            if( this.noEvaluationFlag ) return 0; // 評価しないとき全ての値は0になる
            return this.Y;
        }else if( this.nextIs("0123456789") ){
            var num = parseInt(this.nextChar());
            this.forwardChar(this.nextChar());
            if( num == 0 && this.nextIs("0123456789") ){
                this.throwError("leading-zero");
            }
            while( this.nextIs("0123456789") ){
                num = num * 10 + parseInt(this.nextChar());
                this.checkNum(num);
                this.forwardChar(this.nextChar());
            }
            if( this.noEvaluationFlag ) return 0; // 評価しないとき全ての値は0になる
            return num;
        }
    }else{
        var name = this.var();
        if( this.noEvaluationFlag ) return 0; // 評価しないとき全ての値は0になる
        return this.memory[name];
    }
}
Simulator.prototype.var = function(){
    var key;
    if( this.nextIs('A') ){
        this.forwardChar('A');
        key = "A";
    }else if( this.nextIs('a') ){
        this.forwardWord("a[");
        var ind = this.expr();
        this.forwardChar(']');
        key = "a[" + itos(ind) + "]";
    }else{
        this.throwError("Syntax Error");
    }
    if( this.type != 2 && key == "A" ){
        this.throwError("Variable 'A' can be used only in a query of type 2.");
    }
    if( this.variableA_isUsed && key == "A" ){
        this.throwError("Variable 'A' can be used only once.");
    }
    if( key == "A" ){
        this.variableA_isUsed = true;
    }
    if( key in this.memory ) return key;
    else this.throwError("Invalid variable '"+key+"'.");
}


function report(message) {
  document.getElementById('result').innerHTML += message;
}

function error_report(message) {
  document.getElementById('errorReport').innerHTML += message;
}


function run() {
    document.getElementById('result').innerHTML = "";
    //document.getElementById('errorReport').innerHTML = "";
    var source_ifs = document.getElementById('program').value.split("\n");
    var input_ifs = document.getElementById('inputfile').value.split("\n");
    //LIMIT_EXPR_CALL = parseInt(document.getElementById('exprlimit').value);;
    try{

        line = ""
        var input = ["","",""];
        var lineNumber = [[],[],[]]
        idx = 0;
        lineCnt = 1;

        for(var j=0,len=source_ifs.length;j<len;j++){
            line = source_ifs[j]
            line = clean(line);

            if( line == "----\n" ){
                idx++;
                if( idx == 3 ){

                    error_message = "Invalid format.";
                    throw "";
                }
                lineCnt++;
                continue;
            }

            if( line != "\n" ){
                input[idx] += line; 
                for(var i = 0 ; i &lt; line.length ; i++)
                    lineNumber[idx].push(lineCnt);
            }
            lineCnt++;
        }

        if( idx != 2 ){
            error_message = "Invalid format.";
            throw "";
        }

        // コマンドが全くない
        if( input[0] == "" || input[1] == "" || input[2] == "" ){
            error_message = "Empty command sequence is not allowed";
            console.log(error_message);
            throw "";
        }
        // 末尾の改行を取り除く
        for(i = 0 ; i &lt; 3 ; i++)
            input[i] = input[i].substr(0,input[i].length-1);
        var sim = new Simulator(input,lineNumber);
        tmp = input_ifs[0].split(" ");
        n = parseInt(tmp[0]);
        m = parseInt(tmp[1]);
        q = parseInt(tmp[2]);
        LIMIT_EXPR_CALL = parseInt(tmp[3]);
        sim.setNM(n,m);
        sim.start(0,-1,-1);

        for(var i = 1 ; i &lt;= q ; i++){
            tmp = input_ifs[i].split(" ");
            t = parseInt(tmp[0]);
            x = parseInt(tmp[1]);
            y = parseInt(tmp[2]);
            console.log([t,x,y])
            if( t == 1 ){
                sim.start(1,x,y);
            }else{
                res = sim.start(2,x,y);
                report(res+"\n");
            }
        }
        report("====\n");
        report("Total number of <expr> call: "+itos(sim.exprCalledCount)+"\n");


    }catch(e){
        report("[Error]\n");
        report(error_message+"\n");
        report(e);
    }
}

