### 然后再用for循环（图1）的步骤：
	l=a[0];//记录要交换的值
	for(i=0;i<n;i++)
	{
		x++;//次数++
		swap(a[i],a[i+1]);
		if(l==a[5])break;//当值到达最后一个时，退出运行。
		if(x==n)break;//当执行时次数到达N，退出运行。
	}
#### 完整程序：
	#include<iostream>
	#include<fstream>
	#include<algorithm>
	#include<cmath>
	#include<cstdio>
	#include<string>
	#include<cstring>
	using namespace std;
	int n,i,x,l;
	int a[6]={1,2,3,4,5,6};
	int main()
	{
	cin>>n;
	while(x!=n)
	{
		l=a[0];
		for(i=0;i<n;i++)
		{
			x++;
			swap(a[i],a[i+1]);
			if(l==a[5])break;
			if(x==n)break;
		}
	}
	for(int i=0;i<=5;i++)cout<<a[i];
	cout<<endl;

	return 0;
	}
