```cpp
#include<bits/stdc++.h>
using namespace std;
int hh,mm,ss,n;//对应小时数、分钟数、秒数和输入的秒数
int main(){
    cin>>n;//给定秒数
    hh=n/3600;
    n%=3600;
    mm=n/60;
    n%=60;
    ss=n;
    //以上为得出对应小时、分钟、秒数过程
    if(hh<10){//若不足一位，则输出时先补一个0（下同）
        cout<<0<<hh;
    }else{//够一位
        cout<<hh;
    }
    cout<<":";//别忘了用":"隔开（中间没有空格）
    if(mm<10){
        cout<<0<<mm;
    }else{
        cout<<mm;
    }
    cout<<":";
    if(ss<10){
        cout<<0<<ss;
    }else{
        cout<<ss;
    }
    cout<<endl;//换行保平安
    return 0;
}
```

