using namespace std;
#define ll long long
const int MAXN=11;
int n,m;
int a,b;
bool f[MAXN][MAXN];
int main(){
	cin>>n>>m;
	for(int i=1;i<=m;i++){
		cin>>a>>b;
		f[a][b]=f[b][a]=1;
	}
	for(int i=1;i<=n;i++){
		int ans=0;
		for(int j=1;j<=n;j++){
			bool flag=1;
			for(int k=1;k<=n;k++)
				if(flag&&f[i][k]&&f[j][k]&&i!=j&&!f[i][j])
					ans++,flag=0;
		}
		cout<<ans<<endl;
	}
	return 0;
}
```
