```
#include <bits/stdc++.h>
using namespace std;

int JP[10];
int Island[10];

int main()
{
	for(int i=1;i<=3;i++)
		cin>>JP[i],Island[i] = JP[i];
	sort(JP+1,JP+4);
	for(int i=1;i<=3;i++)
	{
		for(int j=1;j<=3;j++)
			if(JP[j]==Island[i])
			{
				cout<<4-j<<"\n";
				break;
			}
	}
}
