#include<cstdio>
#include<algorithm>
using namespace std;
int a[100007],n,t;
int main(){
	scanf("%d%d",&n,&t);//读入
	long long ans=t;//只要有一个人,就有一个t
	for(int i=1;i<=n;++i)scanf("%d",a[i]);
	sort(a+1,a+1+n);//按时间排序
	for(int i=2;i<=n;++i)ans+=min(a[i]-a[i-1],t);//如果两个人到达间隔的时间超过了时间t,那么就只加上t,如果没到t,就加上间隔
	printf("%lld\n",ans);//输出
	return 0;//好习惯
}
