#include<iostream>
using namespace std;
int n, p, l, r, k, a[101], i, j;//定义在主函数外面自动清零
int main()
{
	cin >> n >> p;
	for(i = 0; i < p; i ++)
	{
		cin >> l >> r >> k;
		for(j = l; j <= r; j ++)//用循环将区间（l,r）内的数替换为k
			a[j] = k;
	}
	for(i = 1; i <= n; i ++)
		cout << a[i] << endl;//换行敲黑板划重点，这个要考的
	return 0;
}//我是可爱的小尾巴
```

