#include<iostream>
#include<algorithm>
using namespace std;
long long a[1010],b[1010];
int main()
{
    int n;
    cin>>n;
    for(int i=1;i<=n;i++)
    {
        cin>>a[i];
        b[i]=a[i];
    }
    sort(a+1,a+1+n);
    for(int i=n;i>=1;i--)
    {
        for(int j=1;j<=n;j++)
        {
            if(a[i]==b[j])
            {
                cout<<j<<endl;
            }
        }
    }
    return 0;
}
```
