#include<bits/stdc++.h>
using namespace std;
int main()
{
	long long n,m;
	cin>>n>>m;
	if((n+m)%3==0||n%3==0||m%3==0) cout<<"Possible"<<endl;//代码的中心就是在这，由题可知——你可以给他们A份或B份或A+B份饼干；其实就是判断A||B||A+B与三的余数是不是为0。是的打印"Possible"。
	else cout<<"Impossible"<<endl;//否则打印"Impossible"
	return 0;
