using namespace std;
string st,st1;
int main()//完美的开始o(∩_∩)o 
{
    cin>>st;//读入
    st1=st.substr(1,st.size()-2);//截取字串的开头和结尾之间的字母
    cout<<st[0]<<st1.size()<<st[st.size()-1];//一个完美的输出o(∩_∩)o 

    return 0;//完美的结束o(∩_∩)o 
}
