#include<algorithm>
#include<cmath>
#include<string>
#include<cstring>
#include<cstdio>
#include<cstdlib>
using namespace std;
int n,m;
char ch[60][60];
int dx[10]={0,-1,-1,-1,0,1,1,1};//x坐标
int dy[10]={-1,-1,0,1,1,1,0,-1};//y坐标
int main()
{
	cin>>n>>m;
	for(int i=1; i<=n; i++)
	for(int j=1; j<=m; j++)
	    cin>>ch[i][j];//读入
	for(int i=1; i<=n; i++)
	{
		for(int j=1; j<=m; j++)
		{
			if(ch[i][j]=='.')//当此位置为‘.’时
			{
				for(int k=0; k<8; k++)//开始搜索
				{
					if(ch[i+dx[k]][j+dy[k]]=='#')
					{
						if(ch[i][j]=='.')ch[i][j]='1';//如果此位还没有加过，就把它变为‘1’。
						else ch[i][j]++;//否则加过了就继续加
					}
				}
				if(ch[i][j]=='.')ch[i][j]='0';//当搜索完毕后此位还是‘.’时，把此位变为‘0’
			}
		}
	}
	for(int i=1; i<=n; i++)
	{
		for(int j=1; j<=m; j++)
		    cout<<ch[i][j];//输出
		cout<<endl;
	}
    return 0;
}



```

