```
#include<cstdio>
#include<cmath>
using namespace std;
int main()
{
	int n,k;
	int ans=1;//初值一定不要忘赋值！！！
	scanf("%d%d",&n,&k);//输入
	for(int i=1;i<=n;i++)//循环n次
