好了整合一下，代码来袭：

	#include<stdio.h>
	using namespace std;
	int a[100005],b[100005];
	int main(){
		int n,i,j;
		scanf("%d",&n);
		a[1]=1;//筛质数
		for(i=2;i<=50000;i++){
			if(a[i]==0){
				for(j=i+i;j<=100000;j+=i)a[j]=1;
			}
		}
		for(i=1;i<=100000;i++){//前缀和
			b[i]=b[i-1];
			if(a[i]==0&&a[(i+1)/2]==0)b[i]++;//判断
		}
		for(i=1;i<=n;i++){//前缀和输出
			int l,r;
			scanf("%d%d",&l,&r);
			printf("%d\n",b[r]-b[l-1]);
		}
		return 0;
	}

