#include<bits/stdc++.h>
using namespace std;
map<int,int>mp;
int main()
{
	int n;
	cin>>n;
	for(int i=1;i<=n;i++)
	{
		int a;
		cin>>a;
		mp[a]=1;
	}
	cout<<mp.size();
	return 0;
}
