int cnt=0;
int main(){
	int l,r;
	cin>>l>>r;
	for(int i=l;i<=r;i++){
		string s=to_string(i);
		string ts=s;
		reverse(s.begin(),s.end());
		if(ts==s){
			cnt++;
		}
	}
	cout<<cnt<<endl;
	return 0;
}
```
