int main(){
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++){
		scanf("%d",&x);
		sum+=x;
		minn=min(minn,x);
	}
	printf("%d\n",n+(m-sum)/minn);
	return 0;
