int main() 
{
	int n;
	scanf("%d", &n);
	if (n <= 104) puts("0");
	else if (n <= 134) puts("1");
	else if (n <= 164) puts("2");
	else if (n <= 188) puts("3");
	else if (n <= 194) puts("4");
	else puts("5");
