    for(i=n;i>=1;i--)
    	for(j=i;j<=n;j++)
    		dp[i][j]=dp[i+1][j]+dp[i][j-1]+a[i][j]-dp[i+1][j-1];
    while(T--)cin>>x>>y,cout<<dp[x][y]<<'\n';
