long long n, t, a, ans;                     //不开long long见祖宗，不过好像这也没必要……
double ch = 0x3f3f3f3f;                     //用来存最小的差值

int main(){
	n = read();
	t = read(), a = read(); //读入n, t, a
	for (int i = 1; i <= n; i++){
		int h = read(); //读入H[i]
		double tem = t - h * 0.006;                //计算当前温度
		if (abs(tem - a) < ch){                    //如果新的差值小于已存的最小差值
			ans = i;
			ch = abs(tem-a);
            //更新答案和最小差值
		}
