### code:
```
#include <cstdio>
#include <cmath>//abs函数头文件 
using namespace std;

int N , T , A , Ans , h[10005];//最大值，下面进行比较大小
double sum = 1e9; 

int main() {
	scanf("%d",&N);
	scanf("%d %d",&T,&A);
	for (int i=1; i<=N; i++ ) {
		scanf("%d",&h[i]);
	}//读入 
	for (int i=1; i<=N; i++ ) {
		double temp = T - h[i] * 0.006;//温度：temperature
		//∵h[i] * 0.006
		//所以temp要用double 
		if (sum > abs(temp - A)) {
			sum = abs(temp - A);
			Ans = i;
		}//比较 
	}
	printf("%d",Ans);//输出 
}
