}
int a[1005][1005],n,m,k,q,mod,x,y,l,dis[1005][10005],ans[1005][1005],sy[1005][1005];
signed main() {
	n=read(),m=read(),l=read();
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			dis[i][j]=1e16;
			ans[i][j]=1e16;
		}
		dis[i][i]=0;
		ans[i][i]=0;
	}//初始化记录长度的dis和记录油数的ans
	FOR(i,1,m){
		int u,v,w;
		u=read(),v=read(),w=read();
		dis[u][v]=w;
		dis[v][u]=w;
	}
	for(int t=1;t<=n;t++){
		for(int i=1;i<=n;i++){
			for(int j=1;j<=n;j++){
				if(dis[i][t]+dis[t][j]<dis[i][j]){
					dis[i][j]=dis[i][t]+dis[t][j];
					dis[j][i]=dis[i][j];
				}
			}
		}
	}//先跑一遍floyd记录最短路
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			if(dis[i][j]<=l){
				ans[i][j]=1;
			}
		}
	}//将可以只用一桶油的存为1
	for(int t=1;t<=n;t++){
		for(int i=1;i<=n;i++){
			for(int j=1;j<=n;j++){
				if(ans[i][t]+ans[t][j]<ans[i][j]){
					ans[i][j]=ans[i][t]+ans[t][j];
					ans[j][i]=ans[i][j];
				}
			}
		}
	}//再跑一遍记录油数
	int q=read();
	FOR(i,1,q){
		int s=read(),t=read();
