void div(int x){
	int cnt=0;
	for(int i=1;cnt<=x;i++)
		if(prime(i))a[++cnt]=i;
}
int find(int x){
    int l=1,r=x;
    while(l<=r){
        int mid=(l+r)/2;
        if(a[mid]>=x)r=mid-1;
        else l=mid+1;
