#include<bits/stdc++.h>
using namespace std;
int n,i,b[1000233];
int main()
{
    cin>>n;
    for(int i=2;i<=1000000;i++)
    {
        if(b[i]) continue;
        for(int j=i;j<=1000000/i;j++) b[i*j]=1;
    }
    i=n;
    while(1)
    {
    	if(!b[i]) {cout<<i;break;}
    	i++;
	}
    return 0;
}
```
