//#define int ll
const int MOD = 1000000007;

signed main() {
    ios_base::sync_with_stdio(false); cin.tie(0);
    ll N; cin >> N;
	int mbit = -1; rep(i, 63) if(N & (1ull<<i)) mbit = i;
	rep(i, 63) if(i <= mbit) N |= (1ull<<i);
	cout << N << endl;
