int fac[2000006], inv[2000006], invfac[2000006];

int calc(int r, int c) {
    // -1 + r+c+2 choose r+1
    return ((1ll * fac[r+c+2] * invfac[r+1] % MOD * invfac[c+1] % MOD) + MOD - 1) % MOD;
}

