    ios_base::sync_with_stdio(false); cin.tie(0);
    int r1, c1, r2, c2; cin >> r1 >> c1 >> r2 >> c2;
    fac[0] = 1; invfac[0] = 1;
    inv[0] = 1; inv[1] = 1;
    rep1(i, r2+c2+2) {
        if(i != 1) inv[i] = 1ll * inv[MOD % i] * (MOD - MOD/i) % MOD;
        fac[i] = 1ll * fac[i-1] * i % MOD;
