//T1 
#include<bits/stdc++.h> 
using namespace std;
#define ll long long
#define f(i,a,b) for(int i=a;i<=b;i++)
inline ll read(){
   int s=0,w=1;
   char ch=getchar();
   while(ch<'0'||ch>'9'){if(ch=='-')w=-1;ch=getchar();}
   while(ch>='0'&&ch<='9') s=s*10+ch-'0',ch=getchar();
   return s*w;
}
#define d read()
ll l,r,k;
int main(){
	k=d,l=d,r=d;
	f(i,l,r)
		if(i%k==0){cout<<"OK";return 0;}
	cout<<"NG";
	return 0;
}

