bool MR_check(ll a, ll p, ll s, int d) 
{
	ll k = pow(a, s, p);
	if (k == 1) 
    return true;
	for (; d; --d, k = mul(k, k, p))
    {
		if (k == p - 1) 
		    return true;
		if (k == 1) 
		    return false;
	}
	return k == 1;
