		ll y = x;
		while (y == x) 
        y = PR_check(rand() % x, x);
		factor_(y, v);
		factor_(x / y, v);
	}
	void factor(ll x, std::vector<ll> &v) 
    {
		v.clear();
		if (!(x & 1)) 
        {
			v.push_back(2);
			x /= (x & -x);
		}
		factor_(x, v);
		std::sort(v.begin(), v.end());
		v.erase(std::unique(v.begin(), v.end()), v.end());
