const int MAXN = 5e3 + 5;
const int mod = 998244353; 

int t[MAXN];
LL dp[MAXN][MAXN];

int main() {
	int n = read(), m = read();
	dp[0][0] = 1;
	for(int i = 1; i <= n; i++) {
		for(int j = 1; j <= n; j++) 
			dp[i][j] = (dp[i - 1][j - 1] + dp[i - 1][j] * ((j - t[i % m] + mod) % mod) % mod) % mod;	
		t[i % m]++;	
	}
	for(int i = 1; i <= n; i++)
		print(dp[n][i], '\n');
	return 0;
}
