```cpp
#include<bits/stdc++.h>
using namespace std;
const int N=200009;
int n,k,x[N],fa[N],ans[N];
vector<int> p[N];
set<int> s;
int main(){
	ios::sync_with_stdio(false);
	cin.tie(0),cout.tie(0);
	cin>>n>>k;
	for(int i=1;i<=n;i++) cin>>x[i],fa[i]=i;
	for(int i=1;i<=n;i++){
		set<int>::iterator it=s.lower_bound(x[i]);
		int id=*it;
		if(it==s.end()){
			s.insert(x[i]);
			p[x[i]].push_back(x[i]);
			if(k==1){
				s.erase(x[i]);
				ans[x[i]]=i;
			}
		}
		else{
			s.erase(id);
			s.insert(x[i]);
			fa[x[i]]=fa[id];
			p[fa[id]].push_back(x[i]);
			int siz=p[fa[id]].size();
			if(siz==k){
				for(int j=0;j<siz;j++) ans[p[fa[id]][j]]=i;
				s.erase(x[i]);
			}
		}
	}
	for(int i=1;i<=n;i++){
		if(ans[i]!=0) cout<<ans[i];
		else cout<<"-1";
		cout<<endl;
	}
	return 0;
}

