		if(sqrt(m - x * x) != (int)sqrt(m - x * x)) continue;
		y = sqrt(m - x * x);
		dx[++td] = x , dy[td] = y;
		dx[++td] = x , dy[td] = -y;
		dx[++td] = -x , dy[td] = y;
		dx[++td] = -x , dy[td] = -y;
	}
	return;
}
void bfs(){}//bfs代码就不写了
int main(){
	scanf("%d%d" , &n , &m);
	getd();
	bfs();
	for(int i = 1; i <= n; i++){
		for(int j = 1; j <= n; j++){
			printf("%d " , dis[i][j]);
		}
