//Open a personal party always stay
#include <bits/stdc++.h>
#define ET return 0
#define fi first
#define se second
#define mp make_pair
#define pb push_back
#define ll long long
#define ull unsigned long long
#define inf INT_MAX
#define uinf INT_MIN
#define pii pair<int,int>
#define pll pair<ll,ll>
#define debug puts("--------Chery AK IOI--------");
#define Yes cout<<"Yes"<<endl;
#define No cout<<"No"<<endl;
#define pt puts("")
#define fr1(i,a,b) for(int i=a;i<=b;i++)
#define fr2(i,a,b) for(int i=a;i>=b;i--)
#define fv(i,p) for(int i=0;i<p.size();i++)
#define ld long double
#define il inline
#define ptc putchar
using namespace std;
const int N=5e5+10;
const int M=5e5;
int Q;
int rt[N];
map <int,int> p;
string s;
int x;
int tnt;
vector <int> ans;
#define mid ((l+r)>>1)
struct Chairman_Segment_Tree{
	int sum[N*50],ls[N*50],rs[N*50],val[N*50];
	void insert(int &p,int las,int l,int r,int d,int v,int v2){
		if(!p){
			tnt++;
			p=tnt;
		}
		sum[p]=sum[las]+v;
		if(l==r){
			val[p]=v2;
			return;
		}
		if(d<=mid){
			insert(ls[p],ls[las],l,mid,d,v,v2);
			rs[p]=rs[las];
		}
		else{
			insert(rs[p],rs[las],mid+1,r,d,v,v2);
			ls[p]=ls[las];
		}
	}
	int binary_query(int p,int l,int r){
		if(l==r){
			return l;
		}
		if(sum[rs[p]]){
			return binary_query(rs[p],mid+1,r);
		}
		else if(sum[ls[p]]){
			return binary_query(ls[p],l,mid);
		}
		return 0;
	}
	int query(int p,int l,int r){
		if(l==r){
			return val[p];
		}
		if(sum[rs[p]]){
			return query(rs[p],mid+1,r);
		}
		else if(sum[ls[p]]){
			return query(ls[p],l,mid);
		}
		return -1;
	}
} T;
#undef mid
int main(){
	cin>>Q;
	fr1(i,1,Q){
//		rt[i]=rt[i-1];
		cin>>s;
		if(s[0]=='A'){
			cin>>x;
			int d=T.binary_query(rt[i-1],1,M);
//			cout<<d<<endl;
			T.insert(rt[i],rt[i-1],1,M,d+1,1,x);
		}
		else if(s[0]=='D'){
			int d=T.binary_query(rt[i-1],1,M);
			if(d==0){
				ans.pb(-1);
				continue;
			}
			T.insert(rt[i],rt[i-1],1,M,d,-1,0);
		}
		else if(s[0]=='S'){
			cin>>x;
			rt[i]=rt[i-1];
			p[x]=i;
		}
		else if(s[0]=='L'){
			cin>>x;
			rt[i]=rt[p[x]];
//			cout<<"!"<<p[x]<<endl;
//			cout<<
		}
		ans.pb(T.query(rt[i],1,M));
//		cout<<<<" ";
	}
//	fr1(i,1,Q){
//		cout<<rt[i]<<" ";
//	}
//	cout<<endl;
	fv(i,ans){
		cout<<ans[i]<<" ";
	}
	cout<<endl;
	ET;
}
