```cpp
#include <bits/stdc++.h>

using namespace std;

const int N = 3001;

int n, m;
int a[N], dp[N][N][2];

int main() {
	scanf("%d %d", &n, &m);
	for (int i = 1; i <= n; i++)
		scanf("%d", &a[i]);

	memset(dp, 0x3f, sizeof dp);
	if (a[1] <= m)
		dp[1][a[1]][1] = 0;

	dp[1][0][0] = 0;
	for (int i = 2; i <= n; i++)
		for (int j = 0; j <= m; j++) {
			dp[i][j][0] = min(dp[i - 1][j][0], dp[i - 1][j][1]);
			if (j >= a[i])
				dp[i][j][1] = min(dp[i - 1][j - a[i]][0] + 1, dp[i - 1][j - a[i]][1]);
		}

	for (int i = 1; i <= m; i++) {
		int ans = min(dp[n][i][0] + 1, dp[n][i][1]);
		printf("%d\n", ans == 0x3f3f3f3f ? -1 : ans);
	}
	return 0;
}
```
