```cpp
#include <bits/stdc++.h>

#define int long long

using namespace std;

const int N = 30;

string s;
int st[N];

signed main() {
	cin >> s;
	int cnt = 1;
	memset(st,false,sizeof st);
	for (int i = 0; i < s.size(); i ++ ) {
		if (s[i] == '(') {
			cnt ++;
		} else if (s[i] >= 'a' && s[i] <= 'z') {
			if (st[s[i] - 'a']) {
				puts("No");
				return 0;
			} else st[s[i] - 'a'] = cnt;
		} else if (s[i] == ')') {
			for (int j = 0; j < 26; j ++ ) if (st[j] == cnt) st[j] = false;
			cnt --;
		}
	}
	cout << "Yes" << endl;
	return 0;
}
```
