| Memory:  1024 MB
| Time:    2000 ms
*******************************/

#include<bits/stdc++.h>
typedef long long ll;
using namespace std;
#define multicase() ll T;scanf("%lld",&T);while(T--)
ll read()
{
	char c;
	ll x=0;
	ll f=1;
	c=getchar();
	while(c<'0'||c>'9')
	{
		if(c=='-')f*=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9')
	{
		x=(x<<1)+(x<<3)+(c^48);
		c=getchar();
	}
	return x*f;
}
ll n,m;
const int maxn=2e5+10;
ll u,v;
ll fa[maxn];
void init()
{
	for(int i=1;i<=n;i++)fa[i]=i;
}
ll find(ll x)
{
	if(fa[x]==x)return x;
	return fa[x]=find(fa[x]);
}
bool merge(ll x,ll y)
{
	ll fx=find(x),fy=find(y);
	if(fx==fy)return 1;
	fa[fx]=fy;
	return 0;
}
int main()
{
	cin>>n>>m;
	ll ans=0;
	init();
	for(int i=1;i<=m;i++)
	{
		cin>>u>>v;
		ans+=merge(u,v);
	}
	printf("%lld\n",ans);
	return 0;
}
```

