int n,m,cnt=0;
int dis[1000005],h[1000005],vst[1000005];
struct edge {
	int to,next,v;
}e[1000005];
void addedge(int x,int y,int z) {
	cnt++;
	e[cnt].to=y;
	e[cnt].v=z;
	e[cnt].next=h[x];
	h[x]=cnt;
}
priority_queue< pair<int,int> > q;
void Dij(int s) {
	for(int i=1;i<=n+m;i++) dis[i]=INF,vst[i]=0;
	dis[s]=0,q.push({-dis[s],s});
	while(!q.empty()) {
		int u=q.top().second;q.pop();
		if(vst[u]) continue;
		vst[u]=1;
		for(int i=h[u];i;i=e[i].next) {
			int y=e[i].to;
			if(dis[y]>dis[u]+e[i].v) {
				dis[y]=dis[u]+e[i].v;
				q.push({-dis[y],y});
			}
		}
	}
}
signed main() {
	scanf("%lld%lld",&n,&m);
	for(int i=1;i<=n;i++) {
		int x;scanf("%lld",&x);
		for(int j=1;j<=x;j++) {
			int y;scanf("%lld",&y);
			addedge(i+m,y,0),addedge(y,i+m,1);
		}
	}
	Dij(1);
	if(dis[m]==INF) puts("-1");
	else printf("%lld",dis[m]-1);
	return 0;
}
