#include <map>
#include <unordered_map>
#include <queue>
#include <stack>
#include <vector>
#include <utility>
#include <cstdlib>
#include <string>
using namespace std;

#define ll long long

const int N = 2e5 + 5, INF = 2e9, MOD = 1e9 + 7;
vector<int> c[N];

inline int read()
{
	int op = 1, x = 0;
	char ch = getchar();
	while ((ch < '0' || ch > '9') && ch != '-') ch = getchar();
	while (ch == '-')
	{
		op = -op;
		ch = getchar();
	}
	while (ch >= '0' and ch <= '9')
	{
		x = (x << 1) + (x << 3) + (ch ^ 48);
		ch = getchar();
	}
	return x * op;
}

inline void write(int x)
{
	if (x < 0) putchar('-'), x = -x;
	if (x > 9) write(x / 10);
	putchar(x % 10 + '0');
}

int n, m, t;
vector<int> G[5 * N];
int dis[N * 5];

int bfs(int u, int v)
{
	queue<int> q;
	q.push(u);
	memset(dis, 0x3f, sizeof dis);
	dis[u] = 0;
	while (q.size())
	{
		int u = q.front();
		q.pop();
		for (int j : G[u])
		{
			if (dis[j] == dis[5 * N - 1])
			{
				dis[j] = dis[u] + 1;
				q.push(j);
			}
	}
	}
	return dis[v];
}

vector<int> v;

int main()
{
	// freopen("*.in", "r", stdin);
	// freopen("*.out", "w", stdout);
	scanf("%d%d", &n, &m);
	for (int i = 1; i <= n; i++)
	{
		int x;
		scanf("%d", &x);
		for (int j = 1; j <= x; j++)
		{
			int k;
			scanf("%d", &k);
			if (k == 1)
			{
				v.emplace_back(i);
			}
			c[i].emplace_back(k);
			G[i].emplace_back(k + n);
			G[k + n].emplace_back(i);
		}
	}
	for (int i : v)
	{
		G[0].emplace_back(i);
		G[i].emplace_back(0);
	}
	int x = bfs(0, m + n);
	if (x == dis[5 * N - 1]) printf("-1\n");
	else printf("%d\n", x / 2 - 1);
	return 0;
}
