int f[1<<25],s[26],mo=1000000007,v[26];
void jr(int a,int b){
	if(b%5!=0&&b%5!=4&&((a&(1<<b-1))!=0)^((a&(1<<b+1))!=0))
		return;
	if(b/5!=0&&b/5!=4&&((a&(1<<b-5))!=0)^((a&(1<<b+5))!=0))
		return;
	f[a]=(f[a]+f[a^(1<<b)])%mo;
}
