	f[0]=1;
	for(int i=1;i<(1<<25);i++){
		int an=0,j=i;
		while(j){
			an+=(j&1);
			j=j>>1;
		}
		an--;
		if(v[an]!=-1){
			if((i&(1<<v[an]))!=0)
				jr(i,v[an]);
		}
		else{
			for(int k=0;k<25;k++)
