#include<bits/stdc++.h>
using namespace std;
namespace my_IO{
	#define ll long long
	#define ull unsigned long long 
	#define ld long double
	template<class T>
	void read(T &num){
		T x=0,f=1;
		char c=getchar();
		while(!isdigit(c)){
			if(c=='-') f=-1;
			c=getchar();
		}
		while(isdigit(c)){
			x=(x<<3)+(x<<1)+c-48;
			c=getchar();
		}
		num=f*x;
	}
	template<class T>
	void write(T x){
		static char buf[40];
		T len=-1;
		if(x<0) x=-x,putchar('-');
		do{
			buf[++len]=x%10+48;
			x/=10;
		}while(x);
		while(len>=0) putchar(buf[len--]);
	}
}
using namespace my_IO;
const int maxn=1e5+10;
int n,m,head[maxn],tot,bk[maxn];
vector<int> v,ans;
struct edge{
	int t,nxt;
}e[maxn<<1];
void add(int st,int ed){
	e[++tot]={ed,head[st]};
	head[st]=tot;
}
void dfs(int u){
	for(int i=head[u];i;i=e[i].nxt){
		if(!bk[e[i].t]){
			bk[e[i].t]=1;
			v.push_back(e[i].t);
			dfs(e[i].t);
			return;
		}
	}
}
void solve(){
	read(n),read(m);
	for(int i=1;i<=m;i++){
		int u,v;
		read(u),read(v);
		add(u,v),add(v,u);
	}
	bk[1]=1; //默认以一号点为 dfs 初始选择的点。
	dfs(1);
	for(int i=(int)v.size()-1;i>=0;i--) ans.push_back(v[i]);
	v.clear();
	ans.push_back(1);
	dfs(1);
	for(int i=0;i<=(int)v.size()-1;i++) ans.push_back(v[i]);
	write((int)ans.size()),putchar('\n');
	int len=ans.size();
	for(int i=0;i<len;i++) write(ans[i]),putchar(' ');
}
int main(){
	int t=1;
	while(t--) solve();
}
```

