		for(int mid=1;mid<lmt;mid<<=1){
			int Wn=qpow(G,(p-1)/(mid<<1));
			if(sgn==-1)Wn=qpow(Wn,p-2);
			for(int j=0,R=mid<<1;j<lmt;j+=R)
