#include <bits/stdc++.h>
#define int long long
#define linf LLONG_MAX
#define iinf INT_MAX
#define ios ios::sync_with_stdio(0);cin.tie(0);
#define N 15
#define M 300005
#define mod 1000000007
#define pint pair<int, int>
#define mp make_pair
using namespace std;
signed main()
{
	ios;
	int n;
	cin >> n;
	cout << "2 3 29995 ";
	if (!(n % 2))
	{
		cout << "30000 ";
		n --;
	}
	n -= 3;
	n /= 2;
	for (int i = 6; n; i ++)
	{
		if (!(i % 2) || !(i % 3) || !(i % 5))
		{
			n --;
			cout << i << ' ' << 30000 - i << ' ';
		}
	}
	cout << '\n';
	return 0;
}
```

