		a[i+1]=s[i]=='?'?-1:s[i]-'0';
	f[1][0][0]=1;
	for(R int i=1;i<=n;++i)// 刷表法
	{
		if(a[i]==1)
		{
			f[i+1][0][0]+=f[i][0][1];
			f[i+1][0][1]+=f[i][0][2];
			f[i+1][1][0]+=f[i][0][0]+f[i][1][1];
			f[i+1][1][1]+=f[i][1][2];
			f[i+1][2][0]+=f[i][1][0]+f[i][2][1]+f[i][2][0];
			f[i+1][2][1]+=f[i][2][2];
		}
		else if(a[i]==0)
		{
			f[i+1][0][1]+=f[i][0][0]+f[i][0][2];
			f[i+1][0][2]+=f[i][0][1];
			f[i+1][1][1]+=f[i][1][0]+f[i][1][2];
			f[i+1][1][2]+=f[i][1][1];
			f[i+1][2][1]+=f[i][2][0]+f[i][2][2];
			f[i+1][2][2]+=f[i][2][1];
		}
		else
		{
			f[i+1][0][0]+=f[i][0][1];
			f[i+1][0][1]+=f[i][0][2];
			f[i+1][1][0]+=f[i][0][0]+f[i][1][1];
			f[i+1][1][1]+=f[i][1][2];
			f[i+1][2][0]+=f[i][1][0]+f[i][2][1]+f[i][2][0];
			f[i+1][2][1]+=f[i][2][2];
			f[i+1][0][1]+=f[i][0][0]+f[i][0][2];
			f[i+1][0][2]+=f[i][0][1];
			f[i+1][1][1]+=f[i][1][0]+f[i][1][2];
			f[i+1][1][2]+=f[i][1][1];
			f[i+1][2][1]+=f[i][2][0]+f[i][2][2];
			f[i+1][2][2]+=f[i][2][1];
		}
		for(R int a=0;a<3;++a)
			for(R int b=0;b<3;++b)
				f[i+1][a][b]%=mod;
	}
	lxl ans=(((f[n+1][0][0]+f[n+1][1][0])%mod+f[n+1][2][0])%mod+f[n+1][2][1])%mod+f[n+1][2][2];
	printf("%lld\n",ans%mod);
	return 0;
}
