int F[N],G[N];

int main(){
	init(N-5);
	cin>>n;
	F[0]=1;
	for(int i=1;i<=n;i++) F[i]=mod-1ll*inv[i]*(mod-inv2)%mod;
	INV::solve(F,F,n);

	G[0]=1;
	for(int i=1;i<=n;i++) G[i]=mod-1ll*F[i]*F[i]%mod;
	INV::solve(G,G,n);

	cout<<1ll*G[n]*mul[n]%mod*mul[n]%mod*pw[n+1]%mod;
}
