inline int read(){
    int X = 0, w = 0 ;
	char ch = 0 ;
    while (!isdigit(ch)) { w |= ch == '-' ; ch = getchar() ; }
    while (isdigit(ch)) X = (X << 3) + (X << 1) + (ch ^ 48), ch = getchar() ;
    return w ? - X : X ;
}

void write(int x){
     if (x < 0) putchar('-'), x = - x ;
     if (x > 9) write(x / 10) ;
     putchar(x % 10 + '0') ;
}

void print(int x) {
	cout << x << endl ;
	exit(0) ;
}

void PRINT(string x) {
	cout << x << endl ;
	exit(0) ;
}

void douout(double x){
	 printf("%lf\n", x + 0.0000000001) ;
}

int n ;
int a[N] ;
string s ;

void get(int x) {
	printf("%d\n", x) ; fflush(stdout) ;
	cin >> s ;
	if (s[0] == 'M') a[x] = 1 ;
	if (s[0] == 'F') a[x] = 2 ;
	if (s[0] == 'V') a[x] = -1, exit(0) ;
}

signed main(){
	cin >> n ;
	int l = 0, r = n - 1 ;
	get(l) ; get(r) ;
	while (l + 1 < r) {
		int mid = (l + r) >> 1 ;
		get(mid) ;
		if ((r - mid - 1) & 1) {
			if (a[mid] == a[r]) r = mid ;
			else l = mid ;
		}
		else {
			if (a[mid] != a[r]) r = mid ;
			else l = mid ;
		}
	}
}

