    using namespace std;
    int n,djy=21000000;//定义变量
    int main()
    {
    cin>>n;//读入n
    for(int i=0; i<=50; i++)//都是买一个的那种，因n<=50，所以i<=50
    {
        for(int j=0; j<=5; j++)//都是买十个的那种，因n<=50，所以j<=5
        {
            if(i+j*10>=n)djy=min(i*15+j*100,djy);//如果当前方案可行，求最小值
        }
    }
    cout<<djy<<endl;//输出结果并换行
        return 0;
    }

