#include<bits/stdc++.h>
using namespace std;
long long fib[42]={0,1,1};//斐波那契数列
int k;
int main(){
	cin>>k;
	for(int i=3;i<=41;i++)
	fib[i]=fib[i-1]+fib[i-2];//求斐波那契数列第i项 
	cout<<fib[k]<<" "<<fib[k+1]<<"\n";//输出 
	return 0; 
}
```
