#include<cstdio>
using namespace std;
long long GET()//读优
{
    long long x=0,f=1;
    char c;
    c=getchar();
    if (c==' ') return x*f;
    while (c<'0'||c>'9') {if (c=='-') f=-1;c=getchar();}
    while (c>='0'&&c<='9') {x=x*10+c-'0';c=getchar();}
    return x*f;
}
int main()
{
	long long n,m,ans=0;
	n=GET();
	m=GET();
	ans=m-n*2;//现找出现有S,就能兑换的C,
	if(ans<0)//小于0说明不能全用完，
	{
		printf("%lld\n",m>>1);//那就只能紧着m用，用m/2个
	} 
	else
	printf("%lld\n",n+(ans>>2));//否则说明用完后还有C，那就再添加剩余m：ans/4个
	return 0;
}
```
