```cpp
#include<cstdio>
#include<iostream>
#include<algorithm>
using namespace std;
int n;
bool flag=false;//flag用来存是否有答案
string s,ans1,ans2,ans3,ans4,ans;
char chg[110];//难点1：chg数组用来进行o与x，S与W的转换
string show(string t)//show函数用来搜答案
{
	for(int i=2;i<=n+1;i++)//搜答案
	{
		char k;
		if(t[i-1]=='S') k=s[i-1];
		else k=chg[s[i-1]];
		if(k=='o') t+=t[i-2];
		else t+=chg[t[i-2]];
	}
	if(t[0]==t[n]&&t[1]==t[n+1]) flag=true;
	/*难点4：看答案的最后一位、答案的第一位、答案的第二位判断真实性*/
	return t;//返回搜索完的字符串
}
int main(){
	cin>>n;
	cin>>s;
	s+=s[0];//难点2：加上陈述的第1位
	s+=s[1];//难点2：加上陈述的第2位
	chg['o']='x';//设置转换函数
	chg['x']='o';//设置转换函数
	chg['S']='W';//设置转换函数
	chg['W']='S';//设置转换函数
	/*难点3：由于羊是S，狼是W，因此羊的字典序更靠前*/
	ans1="SS";//设置初始字符串
	ans3="WS";//设置初始字符串
	ans2="SW";//设置初始字符串
	ans4="WW";//设置初始字符串
